INSERT INTO `t_impuestopreguntas` (`idt_impuestoPreguntas`, `t_impuestoPreguntasTitulo`, `t_impuestoPreguntasTituloObservacion`, `t_impuestoPreguntasAyuda`, `t_impuestoPreguntasPreguntasObservacion`, `t_impuestoPreguntasRespuestasObservacion`) VALUES(11, '¿A dónde elige que vaya a parar el 0,7 % de sus impuestos?\r\n', 'En Relación a la Asignación Tributaria el Cliente manifesta:\r\n', NULL, 'Iglesia Católica@Otros Fines de Interés Social@Las dos Opciones@Ninguna de las dos\r\n', 'A la Iglesia Católica@A Otros Fines de Interés Social@A las dos@A ninguna');
INSERT INTO `t_impuestopreguntas` (`idt_impuestoPreguntas`, `t_impuestoPreguntasTitulo`, `t_impuestoPreguntasTituloObservacion`, `t_impuestoPreguntasAyuda`, `t_impuestoPreguntasPreguntasObservacion`, `t_impuestoPreguntasRespuestasObservacion`) VALUES(12, '¿Nos ha traído los Certificados de Ingresos de las Todas las Empresas en las que ha trabajado?', NULL, 'Con Motivo de la Crisis se está observando que muchas empresas NO declaran correctamente las retenciones o los ingresos y por tanto conviene chequear que los datos de ingresos y retenciones de la Información Fiscal coinciden con los que efectivamente el trabajador ha cobrado, por ejemplo a través del Certificado de Retenciones', NULL, NULL);
INSERT INTO `t_impuestopreguntas` (`idt_impuestoPreguntas`, `t_impuestoPreguntasTitulo`, `t_impuestoPreguntasTituloObservacion`, `t_impuestoPreguntasAyuda`, `t_impuestoPreguntasPreguntasObservacion`, `t_impuestoPreguntasRespuestasObservacion`) VALUES(13, '¿Ha vuelto a trabajar en alguna empresa que le hubiera despedido en los 3 últimos años?', 'El Cliente ha vuelto a vincularse a la Empresa que le despidió hace menos de 3 años y se manifiesta: ', 'Si un trabajador vuelve en menos de TRES AÑOS a la empresa que le despidió, hay que informarle que la Indemnización que en su día cobró y que NO tributó por ella, deja de estar EXENTA y por tanto debemos informarle que debe hacer una declaración complementaria del año del despido y tributar por TODA la Indemnización (Si esta indemnización fue muy alta el Coste Fiscal será MUY GRANDE para el trabajador y por tanto debemos avisarle de ello)', '¿La Empresa que le Despidió hace menos de 3 años es la MISMA o tiene los mismos Socios o la misma Dirección que la que le ha vuelto a Contratar?@¿Ha vuelto a trabajar para la Empresa que le Despidió como Autónomo? (Tampoco hay desvinculación)', 'Se informa al Cliente que la Indemnización que percibió es su día NO ESTA EXENTA y debe Regularizar la Renta del año que cobró la Indemnización para Tributar por ella');
INSERT INTO `t_impuestopreguntas` (`idt_impuestoPreguntas`, `t_impuestoPreguntasTitulo`, `t_impuestoPreguntasTituloObservacion`, `t_impuestoPreguntasAyuda`, `t_impuestoPreguntasPreguntasObservacion`, `t_impuestoPreguntasRespuestasObservacion`) VALUES(14, '¿Paga Cuota de Sindicato?', 'Cuota de Sindicato Pagada Anualmente que manifiesta el Cliente:', NULL, '¿Qué Cuota paga Anualmente?', 'Cuota: ');
INSERT INTO `t_impuestopreguntas` (`idt_impuestoPreguntas`, `t_impuestoPreguntasTitulo`, `t_impuestoPreguntasTituloObservacion`, `t_impuestoPreguntasAyuda`, `t_impuestoPreguntasPreguntasObservacion`, `t_impuestoPreguntasRespuestasObservacion`) VALUES(15, '¿Paga Cuota de Colegio Profesional?', 'Cuota de Colegio Profesional Pagada Anualmente que manifiesta el Cliente:', NULL, '¿Qué Cuota paga Anualmente?@¿La Colegiación es Obligatoria para ejercer la Actividad? (Si no es Obligatoria no se puede deducir la Cuota)\r\n', 'Cuota: ');
INSERT INTO `t_impuestopreguntas` (`idt_impuestoPreguntas`, `t_impuestoPreguntasTitulo`, `t_impuestoPreguntasTituloObservacion`, `t_impuestoPreguntasAyuda`, `t_impuestoPreguntasPreguntasObservacion`, `t_impuestoPreguntasRespuestasObservacion`) VALUES(16, '¿Ha cobrado alguna Pensión del Extranjero?', 'En Relación al Cobro de Pensión en el Extranjero se manifiesta:', NULL, '¿Tiene la Documentación que Acredite el Cobro de la Pensión en el Extranjero? (La AEAT sostiene que si la Pensión ANUAL es Inferior a 1.500 euros se considera 2º Pagador con límite de 22.000 euros)@¿Ha declarado la Pensión en Años Anteriores?@¿Algún hijo se dedujo porque convivía con usted en su Declaración de la Renta?\"\r\n', 'El Cliente NO aporta documentos que acrediten el Cobro TOTAL de la Pensión del Extranjero@El Cliente manifiesta que la cantidad que ha ganado en el año por la Pensión del Extranjero ha sido de XXXXXXX euros');
INSERT INTO `t_impuestopreguntas` (`idt_impuestoPreguntas`, `t_impuestoPreguntasTitulo`, `t_impuestoPreguntasTituloObservacion`, `t_impuestoPreguntasAyuda`, `t_impuestoPreguntasPreguntasObservacion`, `t_impuestoPreguntasRespuestasObservacion`) VALUES(17, '¿Ha sido despedido de alguna Empresa o ha tenido algún Juicio o ha Cobrado algo de un Juicio Anterior?', 'Situación a Informar en Caso de Despido - Juicio - FOGASA, etc:', 'Si un trabajador ha sido despido y calificado su despido como IMPROCEDENTE después del 07 de Julio de 2012, sin que haya existido CONCILIACIÓN ó JUICIO al respecto, hay que informarle que la Indemnización que haya cobrado NO está exenta y por tanto debería venir en la Información Fiscal y haber tenido Retención. Si no viene en la Información Fiscal es porque la Empresa la calificó como EXENTA y esto es INCORRECTO, por tanto hay que Avisarle que tiene la obligación de declarar toda la Indemnización en la Renta (eso sí como Renta Irregular), aunque esto tendrá un coste fiscal alto para el trabajador.\r\nOJO Avisar que ha habido Costas Judiciales a su Favor hay que declararlas como Ganancia Patrimonial de la Base General SIN que se pueda descontar ningún Gasto de Defensa Jurídica', '¿Ha pagado Gastos de Abogado en un Juicio Laboral? (Deducción en la Renta Max. 300 euros)@¿Que Despido ha sido? ¿Le pagaron 20 día por año o 33/45?@¿Viene en los Datos Fiscales la Indemnización? (Si no viene y ha sido Improcedente debe haber existido Conciliación/Juicio)@¿Ha cobrado todo de la Empresa que le Despidió?@¿Ha Cobrado dinero del FOGASA? (Nos tiene que traer la Carta Recibida del FOGASA para ver que parte es Indemnización (Exenta) y que parte Salario (Complementaria)\r\n', 'El Cliente manifiesta que el Despido fue Objetivo (Indemnización 20 días por año)@El Cliente manifiesta que el Despido fue Improcedente y hubo Concilación / Juicio (Indemnización Exenta)@El Cliente manifiesta que el Despido fue Improcedente pero NO hubo conciliación. Se avisa que este Despido NO ESTÁ EXENTO');
INSERT INTO `t_impuestopreguntas` (`idt_impuestoPreguntas`, `t_impuestoPreguntasTitulo`, `t_impuestoPreguntasTituloObservacion`, `t_impuestoPreguntasAyuda`, `t_impuestoPreguntasPreguntasObservacion`, `t_impuestoPreguntasRespuestasObservacion`) VALUES(18, '¿Ha Trabajado en alguna Empresa que NO venga en la Información Fiscal? (P.Ej. como Empleado de Hogar)', 'En Relación al Cobro de Ingresos del Trabajo que NO vienen en la Información Fiscal el Cliente manifiesta:', '', '¿Ha traído el Justificante de los Ingresos a Declarar? (Nóminas, Ingresos Bancarios, etc)@Si es Empleado de Hogar ¿Ha traído el Documento del Alta como Empleado de Hogar? (Ahí viene la Base Declarada de Alta)', 'El Cliente NO puede Aportar Documentos que Acrediten lo que ha cobrado por este concepto@El Cliente manifiesta que lo que ha ganado por este Concepto en TOTAL en el Año ha sido la cantidad de XXXXXXX Euros');
INSERT INTO `t_impuestopreguntas` (`idt_impuestoPreguntas`, `t_impuestoPreguntasTitulo`, `t_impuestoPreguntasTituloObservacion`, `t_impuestoPreguntasAyuda`, `t_impuestoPreguntasPreguntasObservacion`, `t_impuestoPreguntasRespuestasObservacion`) VALUES(19, '¿Ha Cobrado Alguna Pensión Compensatoria Usted o alguno de sus hijos a cargo?', 'En Relación a la Pensión Compensatoria y/o Anualidades por Alimentos el Cliente manifiesta:', 'La Pensión Compensatoria es Rendimiento del Trabajo SIEMPRE para el que la recibe y para el pagador serán favorable fiscalmente si ha sido fijada por Decisión Judicial.\r\nEn las Anualidades por Alimentos para los hijos ESTÁN EXENTAS si son por decisión judicial y si no tributan como Rendimientos del Trabajo y para el Pagador al igual que en la Pensión Compensatoria si son por decisión judicial permiten beneficio fiscal de tributación a tipo separado y si no son por decisión judicial NO benefician', '¿Hay Pensión Compensatoria para el Cónyuge? y si la hay ¿es por Decisión Judicial? (Si es por Decisión Judicial para el Pagador es Deducible en caso contrario no). Para el que la recibe SIEMPRE hay que tributar, salvo que no esté obligado por no llegar al límite (en este caso 12.000 euros)@¿Hay Anualidades por Alimentos para los hijos? Si las hay y son por decisión Judicial para el Pagador es Deducible y los hijos no tienen que declararlo. Si no es por decisión Judicial, el pagador no puede deducir nada y los hijos tienen que tributar como RTP\r\n', ' ');
INSERT INTO `t_impuestopreguntas` (`idt_impuestoPreguntas`, `t_impuestoPreguntasTitulo`, `t_impuestoPreguntasTituloObservacion`, `t_impuestoPreguntasAyuda`, `t_impuestoPreguntasPreguntasObservacion`, `t_impuestoPreguntasRespuestasObservacion`) VALUES(20, '¿Tiene algún ingreso Bancario, Dividendo, etc, que NO venga en los Datos Fiscales? ¿Particip. Preferentes?', 'En Relación a los RC Mobiliario y/o Preferentes se manifiesta lo siguiente:', 'Si el Cliente ha tenido relación con preferentes pueden darse varios casos:\r\n1.- Que haya ido al Arbitraje: Se declara como RCM la diferencia entre Valor de Arbitraje y Valor de Compra sin operaciones intermedias. Posib. Compensación RCM y GGPP\r\n2.- Que haya ido a Juicio: El Juez habrá declarado nulo el contrato y se le devuelve todo menos los intereses pagados anualmente. Lo único que se hace en este caso es valorar el presentar declaración complementaria de TODOS los ejercicios (incluso prescritos) para recuperar las Retenciones soportadas en todos esos años.', 'Si ha tenido Relación con Preferentes ¿Ha Tenido Juicio o Arbitraje?@Si ha tenido Preferentes ¿Vendió las Acciones que Canjeó por las Preferentes o las tiene todavía?@OJO al límite de los 1.600 euros de RCM para NO declarar, porque según una CV hay que contar sólo con los RCM positivos para saber si hay o no obligación de Declarar', 'El Cliente Manifiesta que ya tiene todo canjeado y vendido y que de este tema NO hay nada que declarar.@Se informa al Cliente que los Rendimientos Negativos del Canje de Preferentes que ha tenido, se pueden compensar con el Beneficio que pueda obtener cuando venda las acciones que le han dado a cambio.@Se informa al Cliente que si está esperando Sentencia Judicial o Arbitraje posiblemente tenga que rectificar la Declaración del Año en el que declaró el canje de las preferentes para valorar la operación por el importe que diga la Sentencia.');
INSERT INTO `t_impuestopreguntas` (`idt_impuestoPreguntas`, `t_impuestoPreguntasTitulo`, `t_impuestoPreguntasTituloObservacion`, `t_impuestoPreguntasAyuda`, `t_impuestoPreguntasPreguntasObservacion`, `t_impuestoPreguntasRespuestasObservacion`) VALUES(21, '¿Es propietario de algún Inmueble que tenga Alquilado?', 'En Relación al Arrendamiento de Inmuebles se manifiesta lo siguiente:', 'En Relación al Arrendamiento de Inmuebles se manifiesta lo siguiente:	Hay que meter todos los Ingresos que vengan en el Contrato (AUNQUE NO SE HAYAN COBRADO). Los meses no cobrados irán como Gastos (Saldos de Dudoso Cobro), pero OJO siempre que sean impagados con más de SEIS meses de antigüedad a fecha 31-12. Si no tienen esta antiguedad se tienen que meter en el Ejercicio siguiente. Si finalmente alguna cantidad impagada se cobra se declara en el ejercicio del cobro.', '¿Importe del Alquiler Cobrado en TODO el Año? y si es Local verificar las Retenciones con la Información Fiscal@¿Días que ha estado Alquilado en el Año?@¿Gastos de Reparaciones, Impuestos, Comunidad, Seguros, etc del Inmueble Alquilado?@¿Ha tenido algún mes Impagado?@¿Tenemos el IBI y los datos de Compra del Inmueble Alquilado (Importe de Compra y gastos de la Compra)?@¿El Inmueble Alquilado tiene algún Préstamo que se pidiera para comprar dicho Inmueble o para Reformarlo? (Si el préstamo se pidió para otra cosas los Intereses no serían deducibles)@OJO si está Alquilado y NO se declara porque NO se beneficia de la Reducción del 60% y además por los Consumos y los Anuncios de Internet es fácil de localizar para la AEAT', 'Nos dice el Importe del Alquiler pero no trae las Facturas o Recibos@Ha estado Alquilado TODO el año');
INSERT INTO `t_impuestopreguntas` (`idt_impuestoPreguntas`, `t_impuestoPreguntasTitulo`, `t_impuestoPreguntasTituloObservacion`, `t_impuestoPreguntasAyuda`, `t_impuestoPreguntasPreguntasObservacion`, `t_impuestoPreguntasRespuestasObservacion`) VALUES(22, '¿Tiene algún Inmueble que NO venga en los Datos Fiscales?', 'En Relación a Inmuebles que NO vienen en los Datos Fiscales se manifiesta:', 'Si hay algún Inmueble que no aparezca debe declararle y tener en cuenta que si no está alquilado tributará como Imputación Inmobiliaria al 1,1% si ha sido revisado hace menos de 10 años. En caso contrario al 2%\r\nSi se tiene la Nuda Propiedad de un Inmueble, NO AFECTA en nada en la Declaración, sólo tiene que Declararlo el que tiene el Usufructo', '¿Fecha de Adquisición del Inmueble? (Si es por Herencia es la Fecha de Fallecimiento)@¿Esta a disposición del titular o Arrendado? (OJO a los Consumos y a los Anuncios de Internet de Alquileres)\r\n', 'El Cliente manifiesta Verbalmente que tiene los siguientes Inmuebles@El Cliente manifiesta que ha heredado unos Inmuebles que todavia no aparecen en la Información Fiscal pero que hay que declarar');
INSERT INTO `t_impuestopreguntas` (`idt_impuestoPreguntas`, `t_impuestoPreguntasTitulo`, `t_impuestoPreguntasTituloObservacion`, `t_impuestoPreguntasAyuda`, `t_impuestoPreguntasPreguntasObservacion`, `t_impuestoPreguntasRespuestasObservacion`) VALUES(23, '¿Ejerce algún tipo de Actividad Económica o Profesional?', 'En Relación al Ejercicio de una Actividad Económica se manifiesta:', NULL, '¿En que Año ha comenzado la Actividad? (Importante si es menor de 35 años y es de la Comunidad de Madrid porque hay Deducción Autonómica)@¿Nos trae Libro de Ingresos, Libro de Gastos e Impuestos Presentados?@¿Alguna Inversión en Empresa por Constitución o Ampliación donde adquiera el 40% como capitalista para estar 3 años? (Estudiar posible Ded. CAM)@Revisar si Procede Deducción por Inicio de Actividad (20% para primer y segundo año que tenga beneficios desde el alta)@Revisar si Procede Deducción por TRADE o Actividad para un sólo Cliente (Incompatible en EDS con la Ded. del 5% de Gastos de Difícil Justificación)', ' ');
INSERT INTO `t_impuestopreguntas` (`idt_impuestoPreguntas`, `t_impuestoPreguntasTitulo`, `t_impuestoPreguntasTituloObservacion`, `t_impuestoPreguntasAyuda`, `t_impuestoPreguntasPreguntasObservacion`, `t_impuestoPreguntasRespuestasObservacion`) VALUES(24, '¿Ha vendido Acciones o cualquier otro Producto Financiero que NO venga en los Datos Fiscales?', 'En Relación a la Venta de Acciones se manifiesta:', NULL, '¿Fecha de Compra de las Acciones? ¿Valor de Compra? ¿Gastos de la Compra?@¿Fecha de Venta de las Acciones? ¿Valor de Venta? ¿Gastos de la Venta?@Si tiene Venta de Derechos de Suscripción OJO porque en 2015 y 2016 NO SE DECLARAN, será ganancia a partir de 2017\r\n', ' ');
INSERT INTO `t_impuestopreguntas` (`idt_impuestoPreguntas`, `t_impuestoPreguntasTitulo`, `t_impuestoPreguntasTituloObservacion`, `t_impuestoPreguntasAyuda`, `t_impuestoPreguntasPreguntasObservacion`, `t_impuestoPreguntasRespuestasObservacion`) VALUES(25, '¿Ha transmitido algún Inmueble en el ejercicio (Venta - Donación - Dación en Pago - Ejecución Hip.)?', 'En Relación a la Venta de Inmueble se manifiesta:', 'Recordar varias cosas:\r\n1.- Si es mayor de 65 años y transmite de forma Onerosa o Lucrativa la Vivienda Habitual (o incluso la Nuda Propiedad) está EXENTO.\r\n2.- Si es mayor de 65 años y transmite de Forma Onerosa cualquier bien destinando en 6 meses el importe de la venta a constituir una Renta Vitalicia está EXENTO hasta 240.000 euros de valor venta.\r\n3.- ¿El Inmueble vendido fue comprado antes de 1994? (Posibilidad de Coeficiente de Abatimiento para los primeros 400.000 euros NETOS (descontando gastos) de Valor de Venta)\r\n4.- ¿Ha realizado alguna Dación en Pago de su vivienda? (Está Exenta si era la Vivienda Habitual  y NO tenía otro tipo de Bienes para hacer frente a la Deuda)', '¿El Inmueble Vendido era EXACTAMENTE SU VIVIENDA HABITUAL (o lo ha sido en algún momento de los Últimos TRES años?@¿Datos COMPLETOS de la Venta? (Escrituras, Gastos, Impuestos, Gestoría, Inmobiliaria, Comisiones Cancelación Hipotecas, etc)@En caso de venta de la Viv.Habitual ¿Ha Reinvertido o va a Reinvertir en el plazo de 2 años? (Si no ha reinvertido y lo va a hacer que lo manifieste)@Si ha reinvertido ya ¿Importe del Préstamo Cancelado en la Venta que se corresponda con el Préstamo Original de Compra?@¿Ha realizado MEJORAS en el Inmueble vendido de las cuales tenga Factura?¿Dación en Pago / Ejecución Viv. Hab. En Ult. 4 años?@¿Ha Reclamado la Plusvalía Municipal en caso de Vender un Inmueble con Pérdidas?(Ofrecer el Servicio en nuestro Despacho)', 'El Inmueble transmitido ha sido la Vivienda Habitual del Contribuyente@El Cliente aporta todos los datos de la Compra Original y de la Venta del Inmueble@El Cliente manifiesta que HA REINVERTIDO en la compra de una nueva vivienda habitual y aporta todos los Gastos de la Nueva Compra@En Caso de Reinversión el Cliente también aporta los Datos de Cancelación de la Hipoteca anterior y el porcentaje que esta Hipoteca representaba en la Primera Hipoteca de Adquisición@El Cliente SE COMPROMETE a Reinvertir el importe de la Venta en el Plazo de DOS AÑOS desde la Fecha de venta.@El Cliente manifiesta que el Inmueble Transmitido NO ha estado Afecto dentro de los TRES años Anteriores a la Venta@El Cliente manifiesta que el Importe de la Venta lo va a cobrar a Plazos y por tanto imputará a cada año lo cobrado');
INSERT INTO `t_impuestopreguntas` (`idt_impuestoPreguntas`, `t_impuestoPreguntasTitulo`, `t_impuestoPreguntasTituloObservacion`, `t_impuestoPreguntasAyuda`, `t_impuestoPreguntasPreguntasObservacion`, `t_impuestoPreguntasRespuestasObservacion`) VALUES(26, '¿Tiene Cláusula Suelo - Gastos de Hipoteca que haya reclamado y recibido del Banco?', 'En Relación a Cláusulas Suelo y Gastos de Hipoteca:', 'Si el Cliente tiene Préstamo Hipotecario, Ofrecerle la Reclamación de estos Gastos en nuestro Despacho', 'NO tengo nada de esto@Lo tengo reclamado pero pendiente de Resolución@Si ha existido devolución de Clausula Suelo hay que regularizar solo en tres casos:\r\n          3.1.- Si los Intereses fueron deducidos por Adquisición de Vivienda Habitual (Se regulariza en la Renta del Ejercicio en el que se produce la Dev.)\r\n          3.2.- Si los intereses fueron deducidos como Gasto en RCI y en RAE hay que regularizar mediante declaración complementaria de los ejercicios NO PRESCRITOS\r\n', ' ');
INSERT INTO `t_impuestopreguntas` (`idt_impuestoPreguntas`, `t_impuestoPreguntasTitulo`, `t_impuestoPreguntasTituloObservacion`, `t_impuestoPreguntasAyuda`, `t_impuestoPreguntasPreguntasObservacion`, `t_impuestoPreguntasRespuestasObservacion`) VALUES(27, '¿Ha cobrado algún tipo de Subvención en el Ejercicio?', 'En Relación con el Cobro de Subvenciones el Cliente manifiesta:', 'Subvención por Compra de Vivienda (Si es AEDE se declara en 4 años)\r\nSubvención por Alquiler de Vivienda (Renta de Emancipación)\r\nSubvención por Rehabilitación u Obras de Mejora en la Vivienda\r\nSubvención por Cambio de Bombillas o Iluminación\r\nSubvención por Cambio de Caldera\r\nSubvención por Cambio de Cristales\r\nSubvención por Compra de Vehículo (PLAN PIVE)\r\nSubvención por Instalación de Ascensor (aunque la Tramite y/o Cobre la Comun. de Propietarios)\r\nOJO.- Desde 2015 las Subvenciones Públicas se declaran en el AÑO DEL COBRO', '¿Qué tipo de Subvención a Cobrado?@¿Es Ayuda Estata Directa a la Adquisición de Vivienda (AEDE)?', 'El Cliente aporta Documentación que Acredita que es Ayuda Estatal Directa a la Entrada de Vivienda (AEDE) y por tanto se declara en 4 años@El Cliente aporta Documentación que Acredita que es una Ayuda para Compensar Defectos Estructurales en su Vivienda Habitual y por tanto se declara en 4 años@El Cliente indica que la Ayuda Recibida es el Plan PIVE y Acredita el Importe de la Subvención Recibida@El Cliente indica que la Ayuda Recibida es el Plan PIVE pero NO Acredita su importe simplemente manifiesta que es la Cantidad: XXXXXX@El Cliente manifiesta haber cobrado una Subvención pero NO acredita documentalmente el Importe de la misma. Manifiesta que es: XXXXXX euros');
INSERT INTO `t_impuestopreguntas` (`idt_impuestoPreguntas`, `t_impuestoPreguntasTitulo`, `t_impuestoPreguntasTituloObservacion`, `t_impuestoPreguntasAyuda`, `t_impuestoPreguntasPreguntasObservacion`, `t_impuestoPreguntasRespuestasObservacion`) VALUES(28, '¿Tiene deducción por haber comprado su Vivienda ANTES de 01 de Enero de 2013?', 'El Cliente manifiesta que tiene Derecho a la Deducción por Adquisición de Vivienda anterior al 01 de Enero de 2013:', 'Si la Vivienda Deducida se adquirió con Préstamo y dicho Préstamo Original fue ampliado hay que advertir MUY CLARAMENTE que el total del Préstamo NO es deducible, con independencia de que lleve toda la vida haciendolo y nunca le hayan dicho nada. Si el Declarante quiere expresamente que se mantenga así debemos hacerlo constar en las Observaciones.\r\nIncluir las Cantidades pagadas como Seguro SÓLO si consta como obligatorio en algún documento que tenga el Declarante (por ejemplo la Escritura). Si no es así es mejor NO meterlo porque al NO coincidir la cantidad que tiene Hacienda con la que se va a declarar es posible que le llamen y por tanto si no tiene como Justificarlo es mejor NO deducirlo', '¿La Vivienda fue Adquirida antes del 01 de Enero de 2013? (Después ya NO hay Deducción)@¿La vivienda se Compró con Préstamo Hipotecario y se sigue pagando?@¿El Préstamo original de la compra SE AMPLIÓ alguna vez? Si es así no puede deducirse el 100%@¿Paga algún Seguro Obligatorio al Banco por la Hipoteca que se pueda demostrar?\r\n', 'El Cliente manifiesta que el Préstamo hipotecario que paga está ligado a la Compra de la Vivienda@El Cliente manifiesta que el Préstamo Hipotecario que paga NUNCA fue ampliado@El Cliente manifiesta que el Seguro que paga al Banco por el Inmueble está obligado por la Hipoteca');
INSERT INTO `t_impuestopreguntas` (`idt_impuestoPreguntas`, `t_impuestoPreguntasTitulo`, `t_impuestoPreguntasTituloObservacion`, `t_impuestoPreguntasAyuda`, `t_impuestoPreguntasPreguntasObservacion`, `t_impuestoPreguntasRespuestasObservacion`) VALUES(29, '¿Vive de Alquiler y tiene Contrato Propio a su Nombre ANTERIOR al 01 de Enero de 2015?', 'El Cliente manifiesta que vive de Alquiler y se deja constancia de:', 'La deducción estatal solo es posible si el contrato es anterior a 01-01-2015, además se tiene que haber pagado como mínimo una cuota antes de esa fecha y haber tenido derecho a la Deducción por Alquiler antes de esa fecha, es decir, no haber quedado fuera por ejemplo por superar los Ingresos en Base Imponible.\r\nSi adenás es menor de 35 años y es de la Com. de Madrid y tiene depositada la Fianza o Notificada la Falta de Depósito tendrá derecho a la Deducción por Alquiler Autonómica\r\nObservación (Deducción Estatal: B.Imp. < 24.107,20 (10,05% Ded.) - Ded. CAM: B.Gral.+B.Ahorro < 25.620 / 36.200 (20% Ded.), en caso contrario NO afecta)', '¿Fecha del Contrato (Tiene que ser anterior a 01-01-2015, haber pagado como mínimo una cuota antes de esa fecha y haber tenido derecho a la Deducción por Alquiler antes de esa fecha, es decir, no haber quedado fuera por ejemplo por superar los Ingresos en Base Imponible@¿NIF del Arrendador y Cantidad Anual Pagada@Si es menor de 35 años y es de la Com. de Madrid ¿Tiene depositada la Fianza o Notificada la Falta de Depósito@Observación (Deducción Estatal: B.Imp. < 24.107,20 (10,05% Ded.) - Ded. CAM: B.Gral.+B.Ahorro < 25.620 / 36.200 (20% Ded.), en caso contrario NO afecta)\r\n', 'El Cliente aporta Contrato de Arrendamiento a su nombre del Inmueble en el que vive y los datos del Alquiler@El Cliente NO aporta Contrato de Arrendamiento a su nombre, sólo lo manifiesta@El Cliente Manifiesta que la Fianza ESTÁ DEPOSITADA, pero NO lo Acredita@El Cliente NO ACREDITA ningún dato del Alquiler, manifiesta todos los datos de palabra.');
