INSERT INTO `t_presupuestosconceptos` (`idt_presupuestosConceptos`, `t_presupuestos_idt_presupuestos`, `t_presupuestosConceptosNombres`, `t_presupuestosConceptosDescripcion`, `t_presupuestosConceptoCuota`, `t_presupuestosConceptosOrden`) VALUES(36, 11, 'Asesoramiento Laboral.', 'Una parte esencial de cualquier empresa es la Gestión de Personal, y este apartado es particularmente complejo, pues no sólo la normativa es muy variada sino que su aplicación Judicial lo es más todavía, es decir, los contratos, las bonificaciones, los despidos, en general cualquier acción que se lleve a efecto en relación con el personal si no está correctamente aplicada puede ser un problema en el futuro y puede costar mucho dinero a la empresa.\r\n\r\nNuestra obligación es hacer que el cliente conozca tanto las obligaciones que marca la ley, como las interpretaciones judiciales que cada día se conocen y asi de esta manera tratar de minimizar el riesgo futuro de problemas.\r\n\r\nNosotros ponemos a su disposición el equipo humano, técnico y jurídico necesario para que la Gestión de Personal sea completa, segura y eficaz desde la entrada en la empresa de cualquier trabajador, hasta su posible salida por cualquier motivo.', 'Cuota Fija de 15 euros/ mes y Cuota Variable de 15 euros + IVA / Trabajador / Mes', 4);
